import { useState } from "react";

function CreateMisinformationApp() {
  const [rewardValue, setRewardValue] = useState("");
  const [stringValue1, setStringValue1] = useState("");
  const [stringValue2, setStringValue2] = useState("");

  const handleRewardChange = (event) => {
    setRewardValue(event.target.value);
  };

  const handleString1Change = (event) => {
    setStringValue1(event.target.value);
  };

  const handleString2Change = (event) => {
    setStringValue2(event.target.value);
  };

  return (
    <div>
      <label htmlFor="rewardAmount" style={{ fontSize: "20px", color: "#b83939"}}> Reward for debunking misinformation $ </label>
      <input
        type="number"
        id="rewardAmount"
        value={rewardValue}
        onChange={handleRewardChange}
        style={{ backgroundColor: "gray", padding: "10px", border: "1px solid #ccc", width: "300px"}}
      />
      <br />
      <label htmlFor="stringInput1">String Value 1:</label>
      <input
        type="text"
        id="stringInput1"
        value={stringValue1}
        onChange={handleString1Change}
      />
      <br />
      <label htmlFor="stringInput2">String Value 2:</label>
      <input
        type="text"
        id="stringInput2"
        value={stringValue2}
        onChange={handleString2Change}
      />
    </div>
  );
}

export default CreateMisinformationApp;
