import { useEffect, useState, useCallback } from "react";
import "./App.css";
//import contractABI from './abi.json';
import misInfo from './misInfo';
import misInfoApp from './misInfoApp';
import Web3 from 'web3';

function App() {
  const [walletAddress, setWalletAddress] = useState("");
  const [misinfoContract, setMisinfoContract] = useState();
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const[misInformationMessage, setMisInformationMessage] = useState("");
  const[misInformationReward, setMisInformationReward] = useState("");
  const[misInformationPenalty, setMisInformationPenalty] = useState("");
  const[contractAddresses, setContractAddresses] = useState([]);
  const[appContractPost, setAppContractPost] = useState([]);
  const[appContractSinglePost, setAppContractSinglePost] = useState();

  useEffect(() => {
    getCurrentWalletConnected();
    addWalletListener();
  }, [walletAddress]);

  const fetchData = useCallback(async(contract)=> {
    const appPostMsg = await contract.methods.sendMisinfoAppDetails().call();
    setAppContractSinglePost(appPostMsg)
  }, []);
  
  useEffect(() => {
    fetchData()
  }, [fetchData]);


  const connectWallet = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      try {
        /* MetaMask is installed */
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setWalletAddress(accounts[0]);
        console.log(accounts[0]);

        /* create local contract copy */
        //let contract = new web3.eth.Contract(contractABI, contractAddress);
        let web3 = new Web3(window.ethereum);
        console.log("Anything wrong?");
        const contract = misInfo(web3);
        setMisinfoContract(contract);
        console.log(misinfoContract);

      } catch (err) {
        console.error(err.message);
      }
    } else {
      /* MetaMask is not installed */
      console.log("Please install MetaMask");
    }
  };

  const getCurrentWalletConnected = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_accounts",
        });
        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
          console.log(accounts[0]);
        } else {
          console.log("Connect to MetaMask using the Connect button");
        }
        /* create local contract copy */
        //let contract = new web3.eth.Contract(contractABI, contractAddress);
        let web3 = new Web3(window.ethereum);
        const contract = misInfo(web3);
        setMisinfoContract(contract);
        console.log(misinfoContract);
      } catch (err) {
        console.error(err.message);
      }
    } else {
      /* MetaMask is not installed */
      console.log("Please install MetaMask");
    }
  };

  const addWalletListener = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      window.ethereum.on("accountsChanged", (accounts) => {
        setWalletAddress(accounts[0]);
        console.log(accounts[0]);
      });
    } else {
      /* MetaMask is not installed */
      setWalletAddress("");
      console.log("Please install MetaMask");
    }
  };

  const enterMisinformationTopicHandler = async () => {
    try {
      console.log(misInformationMessage, "\n", walletAddress);
      await misinfoContract.methods.createMisinformationApp(misInformationReward, misInformationMessage, misInformationPenalty).send({
        from: walletAddress
      });
      setSuccessMsg("Transaction Initiated");
      updateState()
    } catch(err) {
      setError(err.message)
      console.log(error);
    }
  }

  const handlePostChange = (e) => {
    setMisInformationMessage(e.target.value);
    //console.log(misInformationMessage);
  };

  const handleRewardChange = (e) => {
    setMisInformationReward(e.target.value);
    //console.log(misInformationMessage);
  };

  const handlePenaltyChange = (e) => {
    setMisInformationPenalty(e.target.value);
    //console.log(misInformationMessage);
  };

  const updateState = () => {
    if (misinfoContract) getContractAddresses();
  }

  const getContractAddresses = async () => {
    //window.location.reload();
    const misinformationContracts = await misinfoContract.methods.getDeployedMisInfos().call();
    setContractAddresses(misinformationContracts);
    let web3 = new Web3(window.ethereum);
    let appContractPosts = [];
    for await (let contract of misinformationContracts) {
      let contractPromise = misInfoApp(web3, contract);
      getMisinformationAppMessage(contractPromise);
      //fetchData(contractPromise);
      console.log('appContractSinglePost: ', appContractSinglePost);
      appContractPosts.push(appContractSinglePost);
    };
    setAppContractPost(appContractPosts);
    //let contract = misInfoApp(web3, misinformationContracts[0]);
    //setMisinfoAppContract(contract);
    //console.log("What is this? ", misinfoAppContract, contract);
    //getMisinformationAppMessage(contract);
  }

  const getMisinformationAppMessage = async (contract) => {
    //const appPostMsg = await contract.methods.sendMisinfoAppDetails().call();
    //console.log('appPostMsg: ' , appPostMsg);
    //setAppContractSinglePost(appPostMsg);
    try{ 
      const appPostMsg = await contract.methods.sendMisinfoAppDetails().call();
      const tempPostMsg = appPostMsg;
      //await setAppContractSinglePost(appPostMsg);
      if (tempPostMsg !== undefined) {
        // Update the state only if the result is not undefined
        setAppContractSinglePost(appPostMsg);
        console.log('data is not undefined');
      } else {
        console.error('Received undefined data.');
      }
      console.log('appPostMsg : ', appPostMsg, 'appContractSinglePost: ', appContractSinglePost)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
    //console.log('appContractSinglePost before return: ', appContractSinglePost);
    //return await appPostMsg;
  }

  return (
    <div>
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            <h1 className="navbar-item is-size-4">Debunking Misinformation through a Decentralized Social Media Platform</h1>
          </div>
          <div id="navbarMenu" className="navbar-menu">
            <div className="navbar-end is-align-items-center">
              <button
                className="button is-white connect-wallet"
                onClick={connectWallet}
              >
                <span className="is-link has-text-weight-bold">
                  {walletAddress && walletAddress.length > 0
                    ? `Connected: ${walletAddress.substring(
                        0,
                        6
                      )}...${walletAddress.substring(38)}`
                    : "Connect Wallet"}
                </span>
              </button>
            </div>
          </div>
        </div>
      </nav>
      <section className="hero is-fullheight">
        <div className="faucet-hero-body">
          <div className="container has-text-centered main-content">
            <h1 className="title is-1">Possible Misinformation that requires debunking! </h1>
            <p>Post evidence information if you believe this event is around a misinformation.</p>
            <div className="box address-box">
              <div className="columns">
                <div className="column is-four-fifths">
                <input
                    className="input is-medium"
                    type="text"
                    placeholder="Enter Reward Amount"
                    onChange={handleRewardChange}
                  />
                  <input
                    className="input is-medium"
                    type="text"
                    placeholder="Enter your post here"
                    onChange={handlePostChange}
                  />
                  <input
                    className="input is-medium"
                    type="text"
                    placeholder="Set Low Quality Rating Cutoff - Scale (1:Low-5:High)"
                    onChange={handlePenaltyChange}
                  />
                </div>
                <div className="column">
                  <button onClick={enterMisinformationTopicHandler} className="button is-link is-medium">
                    Post Evidence
                  </button>
                </div>
                <br></br>
                
              </div>

              <article className="panel is-grey-darker">
                <p className="panel-heading">Existing addresses</p>
                <div className="panel-block">
                  <p></p>
                </div>
              </article>
              <div>
                {appContractPost.map((address, index) => (
                    <div key={index}>
                      <p><b>{(index+1).toString() + ". "}</b>
                        <button>{address}</button>
                      </p>
                      <br></br>
                    </div>
                    ))
                }
                <button onClick={getContractAddresses} className="button is-link is-medium">
                    Refresh to see existing reward scopes
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default App;
