import { ethers } from "ethers";
const appABI = [
	{
		"inputs": [
			{
				"internalType": "uint256",
				"name": "reward",
				"type": "uint256"
			},
			{
				"internalType": "string",
				"name": "misInfoMessage",
				"type": "string"
			},
			{
				"internalType": "address",
				"name": "funder",
				"type": "address"
			},
			{
				"internalType": "uint8",
				"name": "_alpha",
				"type": "uint8"
			}
		],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"inputs": [
			{
				"internalType": "string",
				"name": "post",
				"type": "string"
			}
		],
		"name": "createPost",
		"outputs": [],
		"stateMutability": "nonpayable",
		"type": "function"
	},
	{
		"inputs": [
			{
				"internalType": "address",
				"name": "",
				"type": "address"
			}
		],
		"name": "debunkingUserStruct",
		"outputs": [
			{
				"internalType": "address",
				"name": "debunkUserAddress",
				"type": "address"
			},
			{
				"internalType": "string",
				"name": "debunkPost",
				"type": "string"
			},
			{
				"internalType": "bool",
				"name": "debunkUserEligibleForRewards",
				"type": "bool"
			},
			{
				"internalType": "uint16",
				"name": "debunkerPostRating",
				"type": "uint16"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [
			{
				"internalType": "address",
				"name": "",
				"type": "address"
			}
		],
		"name": "debunkingUsers",
		"outputs": [
			{
				"internalType": "uint8",
				"name": "",
				"type": "uint8"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "sendMisinfoAppDetails",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [
			{
				"internalType": "address",
				"name": "postOwner",
				"type": "address"
			}
		],
		"name": "sendPost",
		"outputs": [
			{
				"components": [
					{
						"internalType": "address",
						"name": "debunkUserAddress",
						"type": "address"
					},
					{
						"internalType": "string",
						"name": "debunkPost",
						"type": "string"
					},
					{
						"internalType": "bool",
						"name": "debunkUserEligibleForRewards",
						"type": "bool"
					},
					{
						"internalType": "uint16",
						"name": "debunkerPostRating",
						"type": "uint16"
					}
				],
				"internalType": "struct MisinformationApp.debunkingUser",
				"name": "",
				"type": "tuple"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];

//const appContract = (web3, contract) => {
//    return new web3.eth.Contract(
//        appABI,
//        contract
//    )
//}

const appContract = (provider, contract) => {
    return new ethers.Contract(
        contract,
		appABI,
		provider
    )
}

export default appContract;
