import { useEffect, useState, useCallback } from "react";
import "./App.css";
import misInfo from './misInfo';
import misInfoApp from './misInfoApp';
import Web3 from 'web3';
import { ethers } from "ethers";

function App() {
  const [walletAddress, setWalletAddress] = useState("");
  const [misinfoContract, setMisinfoContract] = useState();
  const [signer, setSigner] = useState();
  const [misInfoTopicAddresses, setMisInforTopicAddresses] = useState([]);

  const connectWallet = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      try {
        console.log("In connectWallet");
        /* MetaMask is installed */
        const provider = new ethers.providers.Web3Provider(window.ethereum);

        /* get accounts */
        const accounts = await provider.send("eth_requestAccounts", []);

        /* get signer */
        setSigner(await provider.getSigner());

        console.log('What signer', signer);
        /* local contract instance */
        setMisinfoContract(misInfo(provider));

        /* set active wallet address */
        setWalletAddress(accounts[0]);
        console.log("In connectWallet - End");
      } catch (err) {
        console.error(err.message);
      }
    } else {
      /* MetaMask is not installed */
      console.log("Please install MetaMask");
    }
  };

  const getCurrentWalletConnected = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      try {
        console.log("In getCurrentWalletConnected");
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const accounts = await provider.send("eth_accounts", []);
        setSigner(await provider.getSigner());

        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
          console.log(accounts[0]);
        } else {
          console.log("Connect to MetaMask using the Connect button");
        }

        /* local contract instance */
        setMisinfoContract(misInfo(provider));
        console.log("In getCurrentWalletConnected - End");

      } catch (err) {
        console.error(err.message);
      }
    } else {
      /* MetaMask is not installed */
      console.log("Please install MetaMask");
    }
  };

  const addWalletListener = async () => {
    if (typeof window != "undefined" && typeof window.ethereum != "undefined") {
      window.ethereum.on("accountsChanged", (accounts) => {
        setWalletAddress(accounts[0]);
        console.log(accounts[0]);
      });
    } else {
      /* MetaMask is not installed */
      setWalletAddress("");
      console.log("Please install MetaMask");
    }
  };

  const getContractAddresses = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    console.log(signer);
    const misInfoContractSigner = misinfoContract.connect(signer);
    const misinformationContracts = await misInfoContractSigner.getDeployedMisInfos();
    setMisInforTopicAddresses(misinformationContracts);
    console.log(misinformationContracts);
    // setContractAddresses(misinformationContracts);
    // let web3 = new Web3(window.ethereum);
    //let appContractPosts = [];
    //for await (let contract of misinformationContracts) {
     // let contractPromise = misInfoApp(web3, contract);
     // getMisinformationAppMessage(contractPromise);
      //fetchData(contractPromise);
     // console.log('appContractSinglePost: ', appContractSinglePost);
     // appContractPosts.push(appContractSinglePost);
    //};
    //setAppContractPost(appContractPosts);
    //let contract = misInfoApp(web3, misinformationContracts[0]);
    //setMisinfoAppContract(contract);
    //console.log("What is this? ", misinfoAppContract, contract);
    //getMisinformationAppMessage(contract);
  }

  const getMisinformationAppMessage = async (signer) => {
    try{ 
      const appPostMsgSigner = misinfoContract.connect(signer);
      const appPostMsg = await appPostMsgSigner.sendMisinfoAppDetails();
      const tempPostMsg = appPostMsg;
      //await setAppContractSinglePost(appPostMsg);
      if (tempPostMsg !== undefined) {
        // Update the state only if the result is not undefined
        //setAppContractSinglePost(appPostMsg);
        console.log('data is not undefined');
      } else {
        console.error('Received undefined data.');
      }
      console.log('appPostMsg : ', appPostMsg, 'appContractSinglePost: ', appContractSinglePost)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
    //console.log('appContractSinglePost before return: ', appContractSinglePost);
    //return await appPostMsg;
  }

  return (
    <div>
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            <h1 className="navbar-item is-size-4">Debunking Misinformation through a Decentralized Social Media Platform</h1>
          </div>
          <div id="navbarMenu" className="navbar-menu">
            <div className="navbar-end is-align-items-center">
              <button
                className="button is-white connect-wallet"
                onClick={connectWallet}
              >
                <span className="is-link has-text-weight-bold">
                  {walletAddress && walletAddress.length > 0
                    ? `Connected: ${walletAddress.substring(
                        0,
                        6
                      )}...${walletAddress.substring(38)}`
                    : "Connect Wallet"}
                </span>
              </button>
            </div>
          </div>
        </div>
      </nav>
      <section className="hero is-fullheight">
        <div className="faucet-hero-body">
          <div className="container has-text-centered main-content">
            <h1 className="title is-1">Possible Misinformation that requires debunking! </h1>
            <p>Post evidence information if you believe this event is around a misinformation.</p>
            <div className="box address-box">
              <div className="columns">                
                <button onClick={getContractAddresses} className="button is-link is-medium">
                    Refresh to see existing reward scopes
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default App;
